{
    "chart_options": "{
    colors : ["#2E93fA", "#66DA26", "#546E7A", "#E91E63", "#FF9800"],
    stroke : {
        colors : ["#2E93fA", "#66DA26", "#546E7A", "#E91E63", "#FF9800"],
    },
    yaxis : {
        labels : {
            formatter: (value, index, outro) => { 
                if(index.seriesIndex==0) 
                    return value;
                var hours = Math.floor(value / 3600);
                var minutes = Math.floor((value % 3600) / 60);
                return `${hours}h ${minutes}m`;
            },
        },
    },
    toolbar: {
        show    : true,
        offsetX : 0,
        offsetY : 0
    }
}"
}

