{"chart_options":"{
    stroke : {
        colors : ["#2E93fA", "#66DA26", "#546E7A", "#E91E63", "#FF9800"]
    },
    yaxis : {
        labels : {
            formatter: (seconds) => { 
                var hours = Math.floor(seconds / 3600);
                var minutes = Math.floor((seconds % 3600) / 60);
                return `${hours}h ${minutes}m`;
            },
        },
    },
}"}

