elements.commandsql:
SELECT CONCAT(
    (
        SELECT count(*) FROM mdl_course WHERE id > 1
    ),
    ' / ',
    (
        SELECT count(*) FROM mdl_course WHERE id > 1 AND visible = 1
    )
)

